<script type="text/javascript">

function truncate(string, length, delimiter) {
   delimiter = delimiter || "&hellip;";
   return string.length > length ? string.substr(0, length) + delimiter : string;
};

const plugins_wp_org_popular = 'https://www.wpsalenow.com/api/wp-org/plugins/popular/';
const plugins_envato_popular = 'https://www.wpsalenow.com/api/envato/plugins/popular/';

fetch(plugins_wp_org_popular)
.then(res => res.json())
.then(wptalk_plugins_result => {
  <!-- console.log(wptalk_plugins_result); -->
  let output_plugins_total =
    '<p>현재 <b>워드프레스 레포지토리에 등록/승인되어 있는 플러그인 갯수</b>는 총 <a href="https://wordpress.org/plugins/" target="_blank" rel="noreferrer"><b>' + wptalk_plugins_result.info.results.toString().replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1,") + '</b></a>개 (중복 제외)입니다.</p>';

  let output_plugins =
    '<table><thead><tr><th>순위</th><th>이름</th><th>버전</th><th>평점</th><th>링크</th></thead><tbody>';

  for (let t in wptalk_plugins_result.plugins) {
    let wp_org_plugin_url = '';
    if ( wptalk_plugins_result.plugins[t].slug == 'elementor' ) {
      wp_org_plugin_url = 'https://www.wp-talk.com/go/elementor/';
    } else {
      wp_org_plugin_url = 'https://wordpress.org/plugins/' + wptalk_plugins_result.plugins[t].slug;
    }
    id = Number(t) + 1;
    if ( t < 20 ) {
      output_plugins +=
        '<tr><td>' +
        id +
        '</td><td>' +
        '<a href="' + wp_org_plugin_url + '" target="_blank" rel="noopener noreferrer"_>' +
        truncate(wptalk_plugins_result.plugins[t].name, 30) +
        '</a>' +
        '</td><td>' +
        wptalk_plugins_result.plugins[t].version +
        '</td><td>' +
        parseInt(wptalk_plugins_result.plugins[t].rating) + '/100' +
        '</td><td>' +
        '<a href="' + wp_org_plugin_url + '" target="_blank" rel="noopener noreferrer"_>자세히 보기</a>' +
        '</td></tr>';
    }
  }
  output_plugins += '</tbody></table>';
  document.getElementById('data-plugins-wp-org-popular').innerHTML = output_plugins;
  document.getElementById('data-plugins-wp-org-total').innerHTML = output_plugins_total;  
})

fetch(plugins_envato_popular)
.then(res => res.json())
.then(wptalk_plugins_envato_popular_result => {
  <!-- console.log(wptalk_plugins_envato_popular_result); -->
  let output_plugins_envato_popular =
    '<table><thead><tr><th>순위</th><th>이름</th><th>가격(USD)</th><th>판매량</th><th>평점</th><th>링크</th></thead><tbody>';
  for (let t in wptalk_plugins_envato_popular_result) {
    id = Number(t) + 1;
    if ( t < 20 ) {
      output_plugins_envato_popular +=
        '<tr><td>' +
        id +
        '</td><td>' +
        '<a href="https://1.envato.market/c/1957476/275988/4415?u=' + wptalk_plugins_envato_popular_result[t].url + '" target="_blank">' +
        truncate(wptalk_plugins_envato_popular_result[t].item, 30) +
        '</a>' +
        '</td><td>' +
        '$ ' + Math.round( wptalk_plugins_envato_popular_result[t].cost ) +
        '</td><td>' +
        wptalk_plugins_envato_popular_result[t].sales + '개' +
        '</td><td>' +
        Math.round( wptalk_plugins_envato_popular_result[t].rating_decimal*20 ) + '/100' +
        '</td><td>' +
        '<a href="https://1.envato.market/c/1957476/275988/4415?u=' + wptalk_plugins_envato_popular_result[t].url + '" target="_blank">자세히 보기</a>' +
        '</td></tr>';
    }
  }
  output_plugins_envato_popular += '</tbody></table>';
  document.getElementById('data-plugins-envato-popular').innerHTML = output_plugins_envato_popular;
})

</script>

